import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import Chart from "chart.js";

function Title(){
  return (
    <div className="header"> Welcome to Denison Sustainability Dashboard! </div>
  );
}
function getRandomArray(numItems) {
  // Create random array of objects
  let names = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  let data = [];
  for(var i = 0; i < numItems; i++) {
    data.push({
      label: names[i],
      value: Math.round(20 + 80 * Math.random())
    });
  }
  return data;
}

function getRandomDateArray(numItems) {
  // Create random array of objects (with date)
  let data = [];
  let baseTime = new Date('2018-05-01T00:00:00').getTime();
  let dayMs = 24 * 60 * 60 * 1000;
  for(var i = 0; i < numItems; i++) {
    data.push({
      time: new Date(baseTime + i * dayMs),
      value: Math.round(20 + 80 * Math.random())
    });
  }
  return data;
}


function getFeeds() {
  let feeds = [];

  feeds.push({
    title: 'Visits',
    data: getRandomDateArray(150)
  });

  feeds.push({
    title: 'Categories',
    data: getRandomArray(20)
  });

  feeds.push({
    title: 'Categories',
    data: getRandomArray(10)
  });

  feeds.push({
    title: 'Data 4',
    data: getRandomArray(6)
  });

  return feeds;
}

class BarChart extends React.Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
  }

  componentDidMount() {
  this.myChart = new Chart(this.canvasRef.current, {
    type: 'bar',
    data: {
      labels: this.props.data.map(d => d.label),
      datasets: [{
        label: this.props.title,
        data: this.props.data.map(d => d.value),
        backgroundColor: this.props.color
      }]
  }
});

}
render(){
  return (
    <canvas ref={this.chartRef}/>
  );
}

}

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      feeds : getFeeds()
    };
  }
  render() {
    return (
      <div className="Dashboard">
      <BarChart
      data = {this.state.feeds[1].data}
      title = {this.state.feeds[1].title}
      color="#70CAD1"
      />
      </div>
    );
  }
}

class Graph extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      months : 12,
      water : true,
      electric : false,
      solar : false,

    };
  };
  render() {
    return(
      <div className="body2">{[1,2,3]} </div>
    );
  }
}

function Footer() {
  return (
    <div className="footer"> For more information visit www.denison.edu/sustainability </div>
  );
}

class Page extends React.Component{
  render(){
    return(
      <div>
      <Title/>
      <Dashboard />
      <Footer/>
      </div>
    );
  }
}

ReactDOM.render(<Dashboard />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
